mapper.delete:
DELETE 
  FROM REPLIES
 WHERE REPLY_ID = #{replyId}
   AND BOARD_ID = #{boardId}
   AND EMAIL = #{email}

mapper.update:
UPDATE REPLIES
   SET CONTENT = #{content}
     , MDFY_DT = SYSDATE
 WHERE REPLY_ID = #{replyId}
   AND BOARD_ID = #{boardId}
   AND EMAIL = #{email}

UPDATE REPLIES
   SET RECOMMEND_CNT = RECOMMEND_CNT + 1
 WHERE REPLY_ID = #{replyId}
   AND BOARD_ID = #{boardId}
   AND EMAIL != #{email}
